import {wrap} from 'lodash';

/**
 * Creates a new `Defer`red object,
 * @returns {Defer}
 */
export default function Defer() {
  this.promise = new Promise((resolve, reject) => {
    /**
     * @instance
     * @memberof Defer
     * @type {function}
     */
    this.resolve = resolve;
    /**
     * @instance
     * @memberof Defer
     * @type {function}
     */
    this.reject = reject;
  });

  this.promise.then = wrap(this.promise.then, (fn, ...args) => {
    console.trace();
    return Reflect.apply(fn, this.promise, args);
  });
}
