import {assert} from '@ciscospark/test-helper-chai';

import OkMessage from './ok';

describe('ROAP OK Message', () => {
  it('serializes', () => {
    const message = JSON.parse(JSON.stringify(new OkMessage()));
    assert.property(message, 'messageType');
    assert.equal(message.messageType, 'OK');
    assert.property(message, 'seq');
    assert.property(message, 'version');
  });
});
