import RoapMessage from './roap-message';

/**
 * An OK message
 */
export default class OkMessage extends RoapMessage {
  messageType = 'OK';

  /**
   * Indicates if this message is valid according to its
   * {@link RoapMessage#messageType}
   * @see {@link RoapMessage#isValid()}
   * @returns {boolean}
   */
  isValid() {
    return Boolean(this.version === 2 && this.messageType === 'OK');
  }
}
