import SparkPlugin from '../lib/spark-plugin';
import {registerPlugin} from '../spark-core';

const precedence = {
  error: ['log'],
  warn: ['error', 'log'],
  info: ['log'],
  debug: ['info', 'log'],
  trace: ['debug', 'info', 'log']
};

/**
 * Assigns the specified console method to Logger; uses `precedence` to fallback
 * to other console methods if the current environment doesn't provide the
 * specified level.
 * @param {string} level
 * @returns {Function}
 */
function wrapConsoleMethod(level) {
  /* eslint no-console: [0] */
  let impls = precedence[level];
  if (impls) {
    impls = impls.slice();
    while (!console[level]) {
      level = impls.pop();
    }
  }

  return function wrappedConsoleMethod(...args) {
    /* eslint no-invalid-this: [0] */
    /* istanbul ignore if */
    if (process.env.NODE_ENV === 'test' && this.spark && this.spark.internal.device && this.spark.internal.device.url) {
      args.unshift(this.spark.internal.device.url.slice(-3));
    }
    console[level](...args);
  };
}

/**
 * Basic Logger. Pretty much just a wrapper around console. You almost certainly
 * want to import `@ciscospark/plugin-logger`.
 * @class CoreLogger
 */
const Logger = SparkPlugin.extend({
  namespace: 'Logger',
  /**
   * @instance
   * @memberof CoreLogger
   * @method
   * @param {...mixed}
   * @returns {undefined}
   */
  error: wrapConsoleMethod('error'),
  /**
   * @instance
   * @memberof CoreLogger
   * @method
   * @param {...mixed}
   * @returns {undefined}
   */
  warn: wrapConsoleMethod('warn'),
  /**
   * @instance
   * @memberof CoreLogger
   * @method
   * @param {...mixed}
   * @returns {undefined}
   */
  log: wrapConsoleMethod('log'),
  /**
   * @instance
   * @memberof CoreLogger
   * @method
   * @param {...mixed}
   * @returns {undefined}
   */
  info: wrapConsoleMethod('info'),
  /**
   * @instance
   * @memberof CoreLogger
   * @method
   * @param {...mixed}
   * @returns {undefined}
   */
  debug: wrapConsoleMethod('debug'),
  /**
   * @instance
   * @memberof CoreLogger
   * @method
   * @param {...mixed}
   * @returns {undefined}
   */
  trace: wrapConsoleMethod('trace')
});

registerPlugin('logger', Logger);

export default Logger;
